<?phpnamespace h0rseduck\etherscan;/** * Class Client * @package h0rseduck\etherscan */class Client{    /**     * Etherscan API Url     */    const API_URL = "https://api.etherscan.io/api";    /**     * Networks     */    const TESTNET_ROPSTEN = "api-ropsten";    const TESTNET_KOVAN = "api-kovan";    const TESTNET_RINKEBY = "api-rinkeby";    /**     * Tag     */    const TAG_LATEST = "latest";    /**     * Block types     */    const BLOCK_TYPE_BLOCKS = "blocks";    const BLOCK_TYPE_UNCLES = "uncles";    /**     * List block types     * @var array     */    public static $blockTypes = [        self::BLOCK_TYPE_BLOCKS,        self::BLOCK_TYPE_UNCLES    ];    /**     * Etherscan API key.     * @var string     */    private $apiKey;    /**     * Name of the network. Mainnet by default.     * @var string $netName     */    private $network;    /**     * Client constructor.     * @param string $apiKey     * @param null|string $network     */    public function __construct($apiKey, $network = null)    {        $this->apiKey = $apiKey;        $this->network = $network;    }    /**     * @return api\Account     */    public function account()    {        return new api\Account($this);    }    /**     * @return api\Block     */    public function block()    {        return new api\Block($this);    }    /**     * @return api\Contract     */    public function contract()    {        return new api\Contract($this);    }    /**     * @return api\EventLog     */    public function eventLog()    {        return new api\EventLog($this);    }    /**     * @return api\Proxy     */    public function proxy()    {        return new api\Proxy($this);    }    /**     * @return api\Stats     */    public function stats()    {        return new api\Stats($this);    }    /**     * @return api\Token     */    public function token()    {        return new api\Token($this);    }    /**     * @return api\Transaction     */    public function transaction()    {        return new api\Transaction($this);    }    /**     * @return api\Websocket     */    public function websocket()    {        return new api\Websocket($this);    }    /**     * @return string     */    public function getApiKey()    {        return $this->apiKey;    }    /**     * @return string|null     */    public function getNetwork()    {        return $this->network;    }    /**     * Returns API URL     * @param null|string $network     * @return string     */    public static function getAPIUrl($network = null)    {        if (is_null($network)) {            return self::API_URL;        }        return "https://{$network}.etherscan.io/api";    }}